class Solution {
public:
    string largestPalindrome(int n, int k) {
        if(k == 1 || k == 3 || k == 9 || k == 2 || k == 5){
            string ans = "";
            for(int i=0;i<n;i++) ans+='9';
            if(k == 2){
                ans[0] = '8';
                ans[n-1] = '8';
                return ans;
            }
            if(k == 5){
                ans[0] = '5';
                ans[n-1] = '5';
                return ans;
            }
            return ans;
        }
        if(k == 4){
            if(n == 1) return "8";
            if(n == 2) return "88";
            if(n == 3) return "888";
            string ans = "";
            for(int i=0;i<n;i++) ans += '9';
            ans[0] = '8';
            ans[1] = '8';
            ans[n-1] = '8';
            ans[n-2] = '8';
            return ans;
        }
        if(k == 8){
            string ans = "";
            for(int i=0;i<n;i++) ans += '8';
            if(n >= 7){
                for(int i=3;i<n-3;i++) ans[i] = '9';
            }
            return ans;
        }
        if(k == 6){
            if(n == 1) return "6";
            if(n == 2) return "66";
            if(n%2 == 0){
                string ans = "";
                for(int i=0;i<n;i++) ans += '9';
                ans[0] = '8';
                ans[n-1] = '8';
                ans[n/2] = '7';
                ans[n/2 - 1] = '7';
                return ans;
            }
            else{
                string ans = "";
                for(int i=0;i<n;i++) ans += '9';
                ans[0] = '8';
                ans[n-1] = '8';
                ans[n/2] = '8';
                return ans;
            }
        }
        if(k == 7){
            if(n == 1) return "7";
            if(n == 2) return "77";
            if(n%2 == 0){
                string ans = "";
                for(int i=0;i<n;i++) ans += '9';                
                vector<char> temp = {'7', '7', '9', '4', '4', '9'};
                ans[n/2] = temp[((n-2)/2)%6];
                ans[n/2 - 1] = temp[((n-2)/2)%6];
                return ans;
            } 
            else{
                string ans = "";
                for(int i=0;i<n;i++) ans += '9';
                vector<char> temp = {'7', '5', '7', '4', '6', '4'};
                ans[n/2] = temp[(n/2)%6];
                return ans;
            }
        }
        return "0";
    }
};
