class Solution {
public:
    string largestPalindrome(int n, int k) {
        string s;
        if(n==0)return "";
        if(k==1 || k==9 || k==3)return string(n,'9');
        else if(k==2){
            if(n==1)return "8";
            else return "8"+string(n-2,'9')+"8";
        }
        else if(k==4){
            if(n==1)return "8";
            else if(n==2)return "88";
            else if(n==3)return "888";
            else return "88"+string(n-4,'9')+"88"; 
        }
        else if(k==5){
            if(n<=2)return string(n,'5');
            else return "5"+string(n-2,'9')+"5";
        }
        else if(k==8){
            if(n<=6)return string(n,'8');
            else return "888"+string(n-6,'9')+"888";
        }
        else if(k==6){
            if(n&1){
              if(n==1)return "6";
              else return "8"+string((n-3)/2,'9')+"8"+string((n-3)/2,'9')+"8";
            }
            else{
               if(n==2)return "66";
               else if(n==4)return "8778";
               else if(n==6)return "897798";
               else return "8"+string((n-6)/2,'9')+"9779"+string((n-6)/2,'9')+"8";     
            }
        }
        else{
            if(n==1)return "7";
            else if(n==2)return "77";
            else if(n==3)return "959";
            else if(n==4)return "9779";
            else if(n==5)return "99799";
            else if(n==6) return "999999";
            else if(n==7)return "9994999";
            else if(n==8)return "99944999";
            else if(n==9)return "999969999";
            else if(n==10)return "9999449999";
            else if(n==11)return "99999499999";
            else return "999999"+largestPalindrome(n-12,7)+"999999";
        }
        return "";
    }
};
