class Solution {
public:
    bool check(string &n, long long k) {
        long long rem = 0;
        for (auto i : n)
            rem = ((rem * 10) + i - '0') % k;
        return rem == 0;
    }
    string largestPalindrome(int n, int k) {
        string ans(n, '9');
        if(k == 1 or k == 3 or k == 9) return ans;
        if(k == 2){
            ans[0] = '8';
            ans.back() = '8';
            return ans;
        }
        if(k == 4){
            if(n == 1) return "8";
            string res(n, '9');
            res[0] = res[1] = res[n - 1] = res[n - 2] = '8';
            return res;
        }
        if(k == 5){
            ans[0] = '5';
            ans.back() = '5';
            return ans;
        }
        if(k == 6){
            if(n == 1) return "6";
            if(n & 1){
                ans[0] = '8';
                ans.back() = '8';
                ans[n / 2] = '8';
            }
            else{
                if(n <= 2){
                    string res(n, '6');
                    return res;
                }
                else{
                    ans[0] = ans.back() = '8';
                    ans[n / 2 - 1] = ans[n / 2] = '7';
                }
            }
            return ans;
        }
        if(k == 7){
            if(n == 1) return "7";
            if(n & 1){
                string s1((n - 1) / 2, '9');
                string s2 = s1;

                for(int i = 9; i >= 0; i--){
                    string temp = s1 + to_string(i) + s2;
                    if(check(temp, k)) return temp;
                }
            }
            else{
                string s1((n - 2) / 2, '9');
                string s2 = s1;
                for(int i = 99; i >= 0; i-=11){
                    string temp = s1 + to_string(i) + s2;
                    if(check(temp, k)) return temp;
                }
            }
        }
        if(k == 8){
            if(n <= 6){
                string res(n, '8');
                return res;
            }
            ans[0] = ans[1] = ans[2] = ans[n - 1] = ans[n - 2] = ans[n - 3] = '8';
            return ans;
        }
        return ans;
    }
};
