class Solution(object):
    def largestPalindrome(self, n, k):
        """
        :type n: int
        :type k: int
        :rtype: str
        """
        if k==1 or k==3 or k==9:
            return ('9'*n)
        if k==2:
            if n>=3:
                return ('8'+'9'*(n-2)+'8')
            else:
                return '8'*n
        if k==5:
            if n>=3:
                return ('5'+'9'*(n-2)+'5')
            else:
                return ('5'*n)
        if k==4:
            if n<=4:
                return ('8'*n)
            else:
                return ('8'*2 + '9'*(n-4)+'8'*2)
        if k==8:
            if n<=6:
                return ('8'*n)
            else:
                return ('888'+'9'*(n-6)+'888')
        if k==6:
            if n<=2:
                return ('6'*n)
            else:
                nm=n-2
                if nm%2==1:
                    return '8'+'9'*((nm-1)/2)+'8'+'9'*((nm-1)/2)+'8'
                else:
                    return '8'+'9'*((nm-2)/2)+'77'+'9'*((nm-2)/2)+'8'
        if k==7:
            if n<=2:
                return '7'*n
            else:
                if n%2==1:
                    for i in [9,8,7,6,5,4,3,2,1]:
                        ret='9'*((n-1)/2)+str(i)+'9'*((n-1)/2)
                        s=ret
                        if int(ret)%7==0:
                            return s
                else:
                    for i in [9,8,7,6,5,4,3,2,1]:
                        ret='9'*((n-2)/2)+str(i)*2+'9'*((n-2)/2)
                        s=ret
                        if int(ret)%7==0:
                            return s
