class Solution {
public:
    string largestPalindrome(int n, int k) {
        string ans;
        if(k == 1 || k == 3 || k == 9) {
            ans = string(n, '9');
        }
        if(k == 2) {
            ans = string(n, '9');
            ans[0] = '8';
            ans[n-1] = '8';
        }
        if(k == 4 || k == 8) {
            ans = string(n, '9');
            if(n == 1) {
                ans[0] = '8';
            }
            else{
                for(int i = 0; i < 2 + (k==8&&n>2); i ++ ) {
                    ans[i] = '8';
                    ans[n-i-1] = '8';
                }
            }
        }
        if(k == 5) {
            ans = string(n, '9');
            ans[0] = '5';
            ans[n-1] = '5';
        }
        if(k == 6) {
            ans = string(n, '9');
            if(n == 1) {
                return "6";
            }
            if(n == 2) {
                return "66";
            }
            if(n % 2 == 0) {
                ans[0] = '8';
                ans[n-1] = '8';
                ans[n/2] = '7';
                ans[n/2 - 1] = '7';
            }
            if(n % 2 == 1) {
                ans[0] = '8';
                ans[n-1] = '8';
                ans[n/2] = '8';
            }
        }
        if(k == 7) {
            if(n == 1) {
                return "7";
            }
            if(n == 2) {
                return "77";
            }
            // if(n == 3) {
            //     return "959";
            // }
            if(n == 4) {
                return "9779";
            }
            if(n == 5) {
                return "99799";
            }
            if(n == 6) {
                return "999999";
            }
            if(n == 9) {
                return "999969999";
            }
            ans = string(n, '9');
            if(n%6 == 0) {
                return ans;
            }
            if(n % 2 == 0) {
                int aaa = 0;
                for(int i = 9; i >= 0; i -- ) {
                    int res = 0;
                    int tmp = 1;
                    for(int j = 0; j < n; j ++ ) {
                        if((j == n / 2) || (j == n / 2 - 1)) {
                            res = res + i * tmp % 7;
                            // cout << tmp << " " <<i << endl;
                        }
                        else{
                            res = res + 9 * tmp % 7;
                        }
                        tmp = tmp * 10 % 7;
                    }
                    if(res % 7 == 0) {
                        aaa = i;
                        break;
                    }
                }
                ans[n/2] = '0' + aaa;
                ans[n/2-1] = '0' + aaa;

            }
            else{
                int aaa = 0;
                for(int i = 9; i >= 0; i -- ) {
                    int res = 0;
                    int tmp = 1;
                    for(int j = 0; j < n; j ++ ) {
                        if(j == n / 2) {
                            res = res + i * tmp % 7;
                            // cout << tmp << " " <<i << endl;
                        }
                        else{
                            res = res + 9 * tmp % 7;
                        }
                        tmp = tmp * 10 % 7;
                    }
                    if(res % 7 == 0) {
                        aaa = i;
                        break;
                    }
                }
                ans[n/2] = '0' + aaa;
            }
        }
        return ans;
    }
};
