class Solution {
public:
    string largestPalindrome(int n, int k) {
        string res(n, '9');
        if (k == 1 || k == 3 || k == 9) return res;
        if (k == 2) {
            res[0] = '8';
            res.back() = '8';
            return res;
        }
        if (k == 5) {
            res[0] = '5';
            res.back() = '5';
        }
        if (k == 4) {
            if (n == 1) return "8";
            res[0] = '8';
            res[1] = '8';
            res[n - 1] = '8';
            res[n - 2] = '8';
            return res;
        }
        if (k == 6) {
            if (n == 1) return "6";
            if (n == 2) return "66";
            if (n == 3) return "888";
            if (n == 4) return "8778";
            if (n & 1) {
                res[n / 2] = '8';
                res[0] = '8';
                res[n - 1] = '8';
                return res;
            }
            else {
                res[0] = '8';
                res[n - 1] = '8';
                res[n / 2] = '7';
                res[n / 2 + 1] = '9';
                res[n / 2 - 1] = '7';
                res[n / 2 - 2] = '9';
                return res;
            }
        }
        if (k == 8) {
            if (n == 1) return "8";
            if (n == 2) return "88";
            res[0] = '8';
            res[1] = '8';
            res[2] = '8';
            res[n - 1] = '8';
            res[n - 2] = '8';
            res[n - 3] = '8';
            return res;
        }
        if (k == 7) {
            string tmp;
            if (n % 12 == 1) tmp = "7";
            if (n % 12 == 2) tmp = "77";
            if (n % 12 == 3) tmp = "959";
            if (n % 12 == 4) tmp = "9779";
            if (n % 12 == 5) tmp = "99799";
            if (n % 12 == 6) tmp = "999999";
            if (n % 12 == 7) tmp = "9994999";
            if (n % 12 == 8) tmp = "99944999";
            if (n % 12 == 9) tmp = "999969999";
            if (n % 12 == 10) tmp = "9999449999";
            if (n % 12 == 11) tmp = "99999499999";
            int cnt = n / 12;
            string t(6 * cnt, '9');
            return t + tmp + t;
        }
        return res;
    }
};
