class Solution {
public:
    bool isPalindrome(const std::string &s) {
    int left = 0, right = s.size() - 1;
    while (left < right) {
        if (s[left] != s[right]) {
            return false;
        }
        left++;
        right--;
    }
    return true;
}

// Function to find the largest k-palindromic number with n digits
std::string largestKPalindromic(int n, int k) {
    // The largest n-digit number
    int largestNum = std::stoi(std::string(n, '9'));

    // Check down from the largest n-digit number
    for (int num = largestNum; num >= std::pow(10, n - 1); --num) {
        // Convert number to string
        std::string numStr = std::to_string(num);

        // Check if the number is a palindrome and divisible by k
        if (isPalindrome(numStr) && num % k == 0) {
            return numStr;
        }
    }

    // Return an empty string if no such number is found
    return "";
}
    bool isDivisibleBy7(const std::string& numStr , int k) {
    long long mod = 0;
    for (char c : numStr) {
        mod = (mod * 10 + (c - '0')) % k;
    }
    return mod == 0;
}
