class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k in [1, 3, 9]:
            return "9" * n
        if k == 2:
            if n % 2 == 1 and n == 1:
                    return "8"
            else:
                return "8" + "9"*(n-2) + "8"
        if k == 4:
            if n <= 3:
                return "8" * n
            return "8"*2 + "9"*(n-4) + "8"*2
        if k == 8:
            if n <= 6:
                return "8" * n
            return "8"*3 + "9"*(n-6) + "8"*3
        if k == 5:
            if n % 2 == 1:
                if n == 1:
                    return "5"
                return "5" + "9"*(n-2) + "5"
            return "5" + "9"*(n-2) + "5"
        if k == 6:
            if n % 2 == 1:
                if n == 1:
                    return "6"
                nc = (n - 3) // 2
                return "8" + "9"*nc + "8" + "9"*nc + "8"
            if n % 2 == 0:
                if n == 2:
                    return "66"
                nc = (n - 4) // 2
                return "8" + "9"*nc + "77" + "9"*nc + "8"
                #n == 5 => "89898"
                #n == 6 => "897798"
        # 999999
        # 888888
        def div7(s):
            n = 0
            for ch in s:
                n = n*10 + int(ch)
                n %= 7
            return n == 0
        if k == 7:
            if n % 2 == 1:
                nc = (n-1) // 2
                for c in range(9, 1, -1):
                    s = "9" * nc + str(c) + "9" * nc
                    if div7(s):
                        return s
            else:
                nc = (n-2) // 2
                for c in range(9, 1, -1):
                    s = "9" * nc + str(c) + str(c) + "9" * nc
                    if div7(s):
                        return s
        return "0"
