class Solution {
public:
    string largestPalindrome(int n, int k) {
        if((k==1)||(k==9)||(k==3)){
            return string(n,'9');
        } 
        if(k==2){
            if(n<=2)
                return string(n,'8');
            else{
                string ans(n,'9');
                ans[0]='8';
                ans[n-1]='8';
                return ans;
            }
        }
        if(k==5){
            if(n<=2)
                return string(n,'5');
            else{
                string ans(n,'9');
                ans[0]='5';
                ans[n-1]='5';
                return ans;
            }
        }
        if(k==4){
            if(n<=4)
                return string(n,'8');
            else{
                string ans(n,'9');
                ans[0]='8';
                ans[1]='8';
                ans[n-1]='8';
                ans[n-2]='8';
                return ans;
            }
        }
        if(k==8){
            if(n<=6)
                return string(n,'8');
            else{
                string ans(n,'9');
                ans[0]='8';
                ans[1]='8';
                ans[2]='8';
                ans[n-1]='8';
                ans[n-2]='8';
                ans[n-3]='8';
                return ans;
            }
        }
        if(k==6){
            if(n<=2){
                return string(n,'6');
            }
            else{
                string ans(n,'9');
                ans[0]='8';
                ans[n-1]='8';
                if(n%2){
                    ans[n/2]='8';
                }else{
                    ans[n/2]='7';
                    ans[(n/2)-1]='7';
                }
                return ans;
            }
        }
        if(k==7){
            if(n<=2){
                return string(n,'7');
            }

            else{
                string ans(n,'9');
                int a=0,b=0;
                for(int i=0;i<n;++i){
                    if((i%3)==2){
                        if((i/3)%2) a+=999;
                        else b+=999;
                    }
                    else if(i==(n-1)){
                        if((i/3)%2) {
                            if((i%3)==0) a+=9;
                            else a+=99;
                        }else{
                            if((i%3)==0) b+=9;
                            else b+=99;
                        }
                    }
                }

                if((a-b)%7){

                    if(n%2){
                        int d=(n/2)%3;
                        int p=1;
                        while(d>0){
                            p*=10;
                            d--;
                        }
                        for(int i=8;i>=0;--i){
                            if(((n/2)/3)%2){
                                a-=p;
                            }else{
                                b-=p;
                            }
                            ans[n/2]--;
                            if(((a-b)%7)==0){
                                break;
                            }
                        }
                    }else{
                        int d1=(n/2)%3;
                        int p1=1;
                        while(d1>0){
                            p1*=10;
                            d1--;
                        }
                        int d2=((n/2)-1)%3;
                        int p2=1;
                        while(d2>0){
                            p2*=10;
                            d2--;
                        }
                        for(int i=8;i>=0;--i){
                            if(((n/2)/3)%2){
                                a-=p1;
                            }else{
                                b-=p1;
                            }
                            if((((n/2)-1)/3)%2){
                                a-=p2;
                            }else{
                                b-=p2;
                            }
                            ans[n/2]--;
                            ans[(n/2)-1]--;
                            if(((a-b)%7)==0){
                                break;
                            }
                        }
                    }
                }
                //return to_string(a-b);
                return ans;
            }
        }
        return "";
    }
};
