import sys
sys.set_int_max_str_digits(100000)
class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k==1:
            return '9'*n
        elif k==2:
            return '8' if n==1 else '8'+'9'*(n-2)+'8'
        elif k==3:
            return '9'*n
        elif k==4:
            if n==1: return '8'
            elif n==2: return '88'
            elif n==3: return '888'
            return '88'+'9'*(n-4)+'88'
        elif k==5:
            if n==1: return '5'
            return '5'+'9'*(n-2)+'5'
        elif k==6:
            if n==1: return '6'
            if n==2: return '66'
            if n==3: return '888'
            if n%2:
                return '8'+'9'*((n-3)//2)+'8'+'9'*((n-3)//2)+'8'
            return '8'+'9'*((n-4)//2)+'77'+'9'*((n-4)//2)+'8'
        elif k==7:
            if n<=2: return '7'*n
            if n==3: return '959'
            if n%2:
                ans = '9'*n
                mod=(int(ans[:-1])-18)%7
                if mod==0:
                    return ans
                pow = (n-1)//2-1
                for i in range(9):
                    if ((i+1)*(10**pow)-mod)%7==0:
                        break
                ans = '9'*(n//2) + str(9-i-1)+'9'*(n//2)
                return ans
            else:
                ans = '9'*n
                mod=(int(ans[:-1])-18)%7
                if mod==0:
                    return ans
                pow = n//2-2
                for i in range(9):
                    if ((11*(i+1))*(10**pow)-mod)%7==0:
                        break
                ans = '9'*(n//2-1) + str(99-11*(i+1))+'9'*(n//2-1)
                return ans
        elif k==8:
            if n<=4: return '8'*n
            elif n==5: return '88888'
            return '888'+'9'*(n-6)+'888'
        elif k==9:
            return '9'*n
