class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k in [1, 3, 9]:
            return "9" * n
        if k == 2:
            if n <= 2:
                return "8" * n
            else:
                return "8" + "9" * (n-2) + "8"
        if k == 4:
            if n <= 4:
                return "8" * n
            else:
                return "88" + "9" * (n-4) + "88"
        if k == 5:
            if n <= 2:
                return "5" * n
            else:
                return "5" + "9" * (n-2) + "5"
        if k == 6:
            if n < 3:
                return "6" * n
            if n % 2 == 1:
                m = (n - 3) // 2
                return "8" + "9" * m + "8" + "9" * m + "8"
            else:
                m = (n - 4) // 2
                return "8" + "9" * m + "77" + "9" * m + "8"
        if k == 8:
            if n <= 6:
                return "8" * n
            else:
                return "888" + "9" * (n-6) + "888"

        if n <= 2:
            return "7" * n
        if n % 2 == 1:
            m = (n-1) // 2
            for i in range(9, -1, -1):
                num = "9" * m + str(i) + "9" * m
                if is_divisible_by_7(num):
                    return num
        if n % 2 == 0:
            m = (n-2) // 2
            for i in range(9, -1, -1):
                num = "9" * m + str(i) * 2 + "9" * m
                if is_divisible_by_7(num):
                    return num

def is_divisible_by_7(num_str):
    remainder = 0
    for digit in num_str:
        remainder = (remainder * 10 + int(digit)) % 7
    return remainder == 0

