        import sys
        sys.set_int_max_str_digits(100002)
        if k == 1 or k == 9 or k == 3:
            return "9" * n
        if k == 2:
            if n >= 2:
                return "8" + "9" * (n - 2) + "8"
            return "8"

        if k == 4:
            if n > 4:
                return "88" + "9" * (n - 4) + "88"
            return "8" * n

        if k == 5:
            if n >= 2:
                return "5" + "9" * (n - 2) + "5"
            return "5"

        if k == 6:
            if n > 2:
                if n & 1:
                    return "8" + "9" * ((n - 3) // 2) + "8" + "9" * ((n - 3) // 2) + "8"
                else:
                    return (
                        "8" + "9" * ((n - 4) // 2) + "77" + "9" * ((n - 4) // 2) + "8"
                    )

            return "6" * n

        if k == 7:
            if n > 2:
                for j in range(9, 0, -1):
                    m = n - 1
                    p = 1 if n & 1 else 2
                    s = "9" * (m // 2) + str(j) * p + "9" *( m // 2)
                    if s == s[::-1] and int(s) % 7 == 0:
                        return s
            return "7" * n

        if k == 8:
