class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9:
            return "9"*n
        if k == 2:
            if n == 1:
                return "8"
            if n == 2:
                return "88"
            return "8"+"9"*(n-2)+"8"
        if k == 8:
            if n <= 6:
                return "8"*n
            return "888"+"9"*(n-6)+"888"
        if k == 4:
            if n <= 4:
                return "8"*n
            return "88"+"9"*(n-4)+"88"
        if k == 5:
            if n == 1:
                return "5"
            if n == 2:
                return "55"
            return "5"+"9"*(n-2)+"5"
        if k == 6:
            if n == 1:
                return "6"
            if n == 2:
                return "66"
            if n == 3:
                return "888"
            if n == 4:
                return "8778"
            if n == 5:
                return "89898"
            if n == 6:
                return "897798"
            if n == 7:
                return "8998998"
            if n%2 == 1:
                return "8"+"9"*int((n-3)/2)+"8"+"9"*int((n-3)/2)+"8"
            if n%2 == 0:
                return "8"+"9"*int((n-4)/2)+"77"+"9"*int((n-4)/2)+"8"
        if k == 7:
            if n == 1:
                return "7"
            if n == 2:
                return "77"
            if n == 3:
                return "959"
            if n == 4:
                return "9779"
            if n == 5:
                return "99799"
            if n == 6:
                return "999999"
            if n == 7:
                return "9994999"
            if n == 8:
                return "99944999"
            if n == 9:
                return "999969999"
            if n == 10:
                return "9999449999"
            remainder = (n-10) % 12
            hm = {0: 4, 1: 4, 2: 9, 3: 7, 4: 7, 5: 5,
                  6: 7, 7: 7, 8: 9, 9: 4, 10: 4, 11: 6}
            if n % 2 == 1:
                times = int((n-1)/2)
                return "9"*times+str(hm[remainder])+"9"*times
            else:
                times = int((n-2)/2)
                return "9"*times+(str(hm[remainder])*2)+"9"*times
        return "0"
