class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        def cal_sum(num: str):
            num = num[::-1]
            groups = [int(num[i:i+3][::-1]) for i in range(0, len(num), 3)]

            total = 0
            for i, group in enumerate(groups):
                if i % 2 == 0:
                    total += group
                else:
                    total -= group

            return total

        if k in [1, 3, 9]:
            return '9' * n

        if k in [2]:
            if n == 1: return '8'
            else: return '8' + '9' * (n - 2) + '8'

        if k == 4:
            if n == 1: return '8'
            elif n == 2: return '88'
            elif n == 3: return '888'
            elif n == 4: return '8888'
            else: return '88' + '9' * (n - 4) + '88'

        if k == 8:
            if n <= 6: return '8' * n
            else: return '888' + '9' * (n - 6) + '888'

        if k == 5:
            if n == 1: return '5'
            else: return '5' + '9' * (n - 2) + '5'

        if k == 6:
            if n == 1: return '6'
            elif n == 2: return '66'
            elif n % 2:
                nines = (n - 3) // 2 
                return '8' + nines * '9' + '8' + nines * '9' + '8'
            else:
                nines = (n - 4) // 2
                return '8' + nines * '9' + '77' + nines * '9' + '8'

        if k == 7:
            if n == 1: return '7'
            elif n == 2: return '77'
            elif n % 2:
                for i in range(10, 1, -1):
                    nines = (n - 1 ) // 2
                    cand = nines * '9' + str(i) + nines * '9'
                    if cal_sum(cand) % 7 == 0:
                        return cand
            else:
                for i in range(10, 1, -1):
                    nines = (n - 2) // 2
                    cand = nines * '9' + 2 * str(i) + nines * '9'
                    if cal_sum(cand) % 7 == 0:
                        return cand
