class Solution {
public:
    bool isDivisibleBy7(const string &num) {
        long long mod = 0;
        for (char c : num) {
            mod = (mod * 10 + (c - '0')) % 7;
        }
        return mod == 0;
    }
    string largestPalindrome(int n, int k) {        
        string a = "";

        for(int i = 0;i < n;i++){
            a += '9';
        }

        if(k == 1 || k == 3 || k == 9){
            return a;
        }

        if(k == 2){
            if(n == 1){
                return "8";
            }
            a[0] = '8';
            a.back() = '8';
        }
        if(k == 4){
            if(n == 1){
                a[0] = '8';
                return a;
            }
            else{
                a.back() = '8';
                a[n-2] = '8';
                a[0] = '8';
                a[1] = '8';
            }
        }
        if(k == 5){
            a[0] = '5';
            a.back() = '5';
        }
        if(k == 6){
            if(n == 1){
                a[0] = '6';
                return a;
            }
            if(n == 2){
                return "66";
            }
            a[0] = '8';
            a.back() = '8';

            if(n & 1){
                a[n/2] = '8';
            }
            else{
                a[(n/2)] = '7';
                a[(n/2) - 1] = '7';
            }
        }

        if(k == 7){
             for (int i = 0; i < 10; ++i) {
                string candidate = a;
                if (n % 2 == 1) {
                    candidate[n / 2] = '9' - i;
                } else {
                    candidate[n / 2 - 1] = candidate[n / 2] = '9' - i;
                }
                if (isDivisibleBy7(candidate)) {
                    return candidate;
                }
            }
        }

        if(k == 8){
            if(n == 1){
                return "8";
            }
            if(n == 2){
                a[0] = '8';
                a[1] = '8';
            }
            else{
                a.back() = '8';
                a[n-2] = '8';
                a[n-3] = '8';
                a[0] = '8';
                a[1] = '8';
                a[2] = '8';
            }
        }

        return a;
    }
};
