class Solution {
public:
    string largestPalindrome(int n, int k) {
        if(k==1||k==3||k==9){
            string s="";
            for(int i=0;i<n;i++) s+='9';
            return s;
        }
        if(k==2){
            string s="";
            if(n>1) s+='8';
            for(int i=1;i<n-1;i++) s+='9';
            s+='8';
            return s;
        }
        if(k==4){
            string s="";
            for(int i=0;i<n-1;i++) s+='9';
            if(n>1){
                s+='8';
                s[n-2]='8';
                s[0]='8';
                s[1]='8';
            }
            else s+='8';
            return s;
        }
        if(k==8){
            string s="";
            for(int i=0;i<n-1;i++) s+='9';
            if(n>1){
                s+='8';
                s[n-2]='8';
                if(n-3>=0) s[n-3]='8';
                s[0]='8';
                s[1]='8';
                if(2<n) s[2]='8';
            }
            else s+='8';
            return s;
        }
        if(k==5){
            string s="";
            for(int i=0;i<n-1;i++) s+='9';
            if(n>1) s[0]='5';
            s+='5';
            return s;
        }
        if(k==6){
            string s="";
            if(n==1) return "6";
            if(n==2) return "66";
            if(n==3) return "888";
            if(n%2){
                for(int i=0;i<n;i++) s+='9';
                s[0]='8';
                int mid=n/2;
                s[mid]='8';
                s[n-1]='8';
            }
            else{
                int mid=n/2;
                s+='8';
                for(int i=1;i<mid-1;i++) s+='9';
                s+='7', s+='7';
                for(int i=mid+1;i<n-1;i++) s+='9';
                s+='8';
            }
            return s;
        }
        string s="";
        for(int i=0;i<n;i++) s+='9';
        if(n%12==1||n%12==5){
            int mid=n/2;
            s[mid]='7';
            return s;
        }
        if(n%6==0||n%12==0){
            return s;
        }
        if(n%12==2||n%12==4){
            int mid=n/2;
            s[mid]='7';
            s[mid-1]='7';
            return s;
        }
        if(n%12==7||n%12==11){
            int mid=n/2;
            s[mid]='4';
            return s;
        }
        if(n%12==8||n%12==10){
            int mid=n/2;
            s[mid]='4';
            s[mid-1]='4';
            return s;
        }
        if(n%12==3){
            int mid=n/2;
            s[mid]='5';
        }
        if(n%12==9){
            int mid=n/2;
            s[mid]='6';
        }
        return s;
    }
};
