table = { (0,0):""}
for i in range(10**6):
    if str(i)==str(i)[::-1]:
        key = (len(str(i)) , i%7)
        table[key] = str(i)


class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        def seven(n):
            for a in range( n//2,-1,-1):
                b = n - a*2
                mod = (10**a-1)*(1+10**(a+b))
                mod = (-mod)%7

                # mod is how much you need 10^(?) * X to be mod 7
                # 10**a



                mod7Inverse = [0,1,4,5,2,3,6]
                # i * mod7Inverse[i] == 1 mod7

                # 10**a * x === -(mod)
                x = (mod7Inverse[ (10**a)%7 ] * mod) % 7
                if (b,x) in table:
                    return "9"*a + table[(b,x)] + "9"*a


        if k==1 or k==3:
            return "9"*n
        if k==2:
            if n==1:
                return "8"
            return "8"+"9"*(n-2)+"8"
        if k==4:
            if n <= 4:
                return "8"*n
            return "8"*2 + "9"*(n-4)+"8"*2

        if k==5:
            if n==1:
                return "5"
            else:
                return "5"+"9"*(n-2)+"5"

        if k==6:
            if n==1:
                return "6"
            if n==2:
                return "66"
            if n==3:
                return "888"
            if n%2==0:
                s = "8" + "9"*( (n//2-2) ) + "7"
                return s + s[::-1]
            if n%2==1:
                s = "8" + "9"*( n//2 - 1)
                return s + "8" + s[::-1]

        if k==7:
            return seven(n)        

        if k==8:
            if n <= 6:
                return "8"*n
            else:
                return "8"*3+"9"*(n-6)+"8"*3

        if k==9:
            return "9"*n
