class Solution {
public:
    string largestPalindrome(int n, int k) {
        if (k == 1 || k == 3 || k == 9) {
            return string(n, '9');
        }
        if (k == 2) {
            string base = string(n, '9');
            base[0] = '8';
            base[n - 1] = '8';
            return base;
        }
        if (k == 4) {
            if (n < 5) return string(n, '8');
            else {
                string base = string(n, '9');
                base[0] = '8';
                base[n - 1] = '8';
                base[1] = '8';
                base[n - 2] = '8';
                return base;
            }
        }
        if (k == 8 || k == 4) {
            if (n < 7) return string(n, '8');
            else {
                string base = string(n, '9');
                base[0] = '8';
                base[1] = '8';
                base[2] = '8';
                base[n - 1] = '8';
                base[n - 2] = '8';
                base[n - 3] = '8';
                return base;
            }
        }
        if (k == 6) {
            if (n == 1) {
                return "6";
            }
            if (n == 2) {
                return "66";

            }
            if (n == 3) {
                return "888";
            }
            string base = string(n, '9');
            base[0] = '8';
            base[n - 1] = '8';
            if (n & 1) {
                base[(n - 1) / 2] = '8';
            }
            else {
                base[n / 2] = '7';
                base[n / 2 - 1] = '7';
            }
            return base;
        }

        if (k == 5) {
            string base = string(n, '9');
            base[0] = '5';
            base[n - 1] = '5';
            return base;
        }

        // 7
        if (n == 1) return "7";
        if (n == 2) return "77";
        string base = string(n, '9');
        vector<string> cmap = {
            "7",
            "77",
            "5",
            "77",
            "7",
            "",
            "4",
            "44",
            "6",
            "44",
            "4",
            "",
        };
        string mine = cmap[(n - 1) % 12];
        if (n & 1) {
            base[(n - 1) / 2] = mine[0];
        }
        else {
            if (mine.size()) {
                base[n / 2] = mine[0];
                base[n / 2 - 1] = mine[1];
            }
        }
        return base;
    }
};
