def chu7(number_str):
    r = 0
    for digit in number_str:
        r = (r * 10 + int(digit)) % 7
    return r == 0
class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 3 or k == 1 or k == 9: return '9' * n
        if k == 2: return '8' + '9' * (n - 2) + ('8' if n > 1 else '')
        if k == 4:
            if n <= 4: return '8' * n
            return '88' + '9' * (n - 4) + '88'
        if k == 5: return '5' + '9' * (n - 2) + ('5' if n > 1 else '')
        if k == 6:
            if n == 1: return '6'
            if n == 2: return '66'
            if n == 3: return '888'
            if n % 2 == 1:
                s = '8' + '9' * (n // 2 - 1)
                sum1 = ((8 + 9 * (n // 2 - 1)) * 2) % 3
                ans = s + str(9 - sum1) + s[::-1]
                return ans
            s = '8' + '9' * (n // 2 - 2)
            sum1 = ((8 + 9 * (n // 2 - 2)) * 2) % 3
            mid = 0
            if sum1 == 1: mid = '77'
            if sum1 == 2: mid = '88'
            if sum1 == 0: mid = '99'
            ans = s + mid + s[::-1]
            return ans
        if k == 8: 
            if n <= 6: return '8' * n
            return '888' + '9' * (n - 6) + '888'
        if n <= 2: return '7' * n
        if n % 2 == 0:
            s = '9' * (n // 2 - 1)
            for x in ('99', '88', '77', '66', '55', '44', '33', '22', '11', '00'):
                if chu7(s + x + s): return s + x + s
        if n % 2:
            s = '9' * (n // 2)
            for x in ('9', '8', '7', '6', '5', '4', '3', '2', '1', '0'):
                if chu7(s + x + s): return s + x + s
        return '7' * n
