class Solution {
public:
string constructLargestPalindrome(int n) {
    string palindrome;
    if (n % 2 == 0) {
        palindrome = string(n / 2, '9');
        palindrome += string(palindrome.rbegin(), palindrome.rend());
    } else {
        palindrome = string(n / 2, '9');
        palindrome += '9';
        palindrome += string(palindrome.rbegin() + 1, palindrome.rend()); 
    }
    return palindrome;
}

void decrementPalindrome(string &palindrome) {
    int len = palindrome.size();
    int i = (len - 1) / 2;
    while (i >= 0 && palindrome[i] == '0') {
        palindrome[i] = '9';
        palindrome[len - 1 - i] = '9';
        i--;
    }
    if (i >= 0) {
        palindrome[i]--;
        palindrome[len - 1 - i] = palindrome[i];
    }
}
string findLargestPalindromeDivisibleBy7(int n) {
    string palindrome = constructLargestPalindrome(n);
    while (true) {
        long long mod = 0;
        for (char digit : palindrome) {
            mod = (mod * 10 + (digit - '0')) % 7;
        }

        if (mod == 0) {
            return palindrome;
        }
        decrementPalindrome(palindrome);
    }
}

    string largestPalindrome(int n, int k) {
