class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        sys.set_int_max_str_digits(10000000)

        def genpalin(n):
            if n <= 0:
                return

            palindromes = []

            if n == 1:
                for i in range(9,0,-1):
                    yield str(i)
                return

            if n % 2 == 0:
                half_length = n // 2
                start = 10**half_length - 1
                end = 10**(half_length - 1) - 1
                for i in range(start, end, -1):
                    s = str(i)
                    yield s + s[::-1]
            else:
                half_length = n // 2
                start = 10**(half_length) - 1
                end = 10**(half_length-1) - 1
                for i in range(start, end, -1):
                    s = str(i)
                    for digit in range(9, -1, -1):
                        yield s + str(digit) + s[::-1]

        if k == 1:
            return "9"*n
        if k == 2:
            if n == 1: return "8"
            return "8"+"9"*(n-2)+"8"
        if k == 3:
            return "9"*n
        if k == 4:
            if n == 1: return "8"
            if n == 2: return "88"
            if n == 3: return "888"
            return "88" + "9"*(n-4) + "88"
        if k == 5:
            if n == 1: return "5"
            return "5"+"9"*(n-2)+"5"
        if k == 6:
            if n == 1:
                return "6"
            if n == 2:
                return "66"

            if n%2 == 1:
                return "8"+"9"*((n-3)//2)+"8"+"9"*((n-3)//2)+"8"
            else:
                return "8"+"9"*((n-4)//2)+"77"+"9"*((n-4)//2)+"8"
        if k == 7:
            if n == 1: return "7"

            for p in genpalin(n):
                if int(p)%7 == 0:
                    return str(p)
        if k == 8:
            if n < 6:
                return "8"*n
            return "888"+"9"*(n-6)+"888"
        if k == 9:
            return "9"*n
