class Solution {
public:
    string largestPalindrome(int n, int k) {
        if (k == 1 || k == 3 || k == 9) {
            string s;
            while (n--) s += '9';
            return s;
        }
        if (k == 2) {
            if (n == 1) return "8";
            string s;
            n -= 2;
            while (n--) s += '9';
            return "8" + s + "8";
        }
        if (k == 4) {
            if (n == 1) return "8";
            if (n == 2) return "88";
            if (n == 3) return "888";
            string s;
            n -= 4;
            while (n--) s += '9';
            return "88" + s + "88";
        }
        if (k == 5) {
            if (n == 1) return "5";
            string s;
            n -= 2;
            while (n--) s += '9';
            return "5" + s + "5";
        }
        if (k == 6) {
            if (n == 1) return "6";
            if (n == 2) return "66";
            string s;
            if (n % 2 == 1) {
                n = n / 2 - 1;
                while (n--) s += '9';
                s = "8" + s + "8" + s + "8";
            } else {
                n = n / 2 - 2;
                while (n--) s += '9';
                s = "8" + s + "77" + s + "8";
            }
            return s;
        }
        if (k == 7) {
            string s = "0";
            vector<int> vc(n);
            vc[0] = 1;
            for (int i = 1; i < n; ++i) {
                vc[i] = vc[i - 1] * 10 % 7;
                s += '0';
            }
            int c = 0, i = 0;
            while ((n - i - 1) - i > 1) {
                s[i] = s[n - i - 1] = '9';
                c = (c + (vc[i] + vc[n - i - 1]) * 9) % 7;
                ++i;
            }
            if (i == n - i - 1) {
                for (int j = 9; j >= 0; --j)
                    if ((c + vc[i] * j) % 7 == 0) {
                        s[i] = j + '0';
                        break;
                    }
            } else {
                for (int j = 9; j >= 0; --j)
                    if ((c + (vc[i] + vc[i + 1]) * j) % 7 == 0) {
                        s[i] = s[i + 1] = j + '0';
                        break;
                    }
            }
            return s;
        }
        if (k == 8) {
            if (n == 1) return "8";
            if (n == 2) return "88";
            if (n == 3) return "888";
            if (n == 4) return "8888";
            if (n == 5) return "88888";
            string s;
            n -= 6;
            while (n--) s += '9';
            return "888" + s + "888";
        }
        return "ERROR";
    }
};
