class Solution {
public:


    string largestPalindrome(int n, int k) {
        if (k == 1 || k == 9 || k == 3){
            string temp = "";
            for (int i = 0; i < n; i++){
                temp += '9';
            }
            return temp;
        }
        if (k == 2 || k == 4){
            string temp = "";
            for (int i = 0; i < n; i++){
                temp += '9';
            }
            temp[0] = '8';
            temp[n - 1] = '8';
            if (k == 4){
                if (n > 2){
                    temp[1] = '8';
                    temp[n - 2] = '8';
                }
            }
            return temp;
        }
        if (k == 5){
            string temp = "";
            for (int i = 0; i < n; i++){
                temp += '9';
            }
            temp[0] = '5';
            temp[n - 1] = '5';
            return temp;
        }
        if (k == 6){
            if (n <= 2){
                string temp = "";
                for (int i = 0; i < n; i++){
                    temp += '6';
                }
                return temp;
            }
            string temp = "";
            for (int i = 0; i < n; i++){
                temp += '9';
            }
            temp[0] = '8';
            temp[n - 1] = '8';
            if (n % 2){
                temp[n / 2] = '8';
            }
            else{
                temp[n / 2] = '7';
                temp[n/2 - 1] = '7';
            }
            return temp;
        }
        if (k == 7){
            string temp = "";
            for (int i = 0; i < n; i++){
                temp += '9';
            }
            int num = 0, rem = 0, temp2 = 1;
            for (int i = n-1; i >= 0; i--){
                if ((n % 2 == 1 && i == n / 2) || (n % 2 == 0 && (i == n / 2 || i == n / 2 - 1))){
                    rem += temp2;
                    rem %= 7;
                }
                else{
                    num += temp2 * (temp[i] - '0');
                    num %= 7;
                }
                temp2 *= 10;
                temp2 %= 7;
            }
            for (int i = 0; i < 10; i++){
                if ((num + rem * i) % 7 == 0){
                    temp[n/2] = (i + '0');
                    if (n % 2 == 0){
                        temp[n/2 - 1] = (i + '0');
                    }
                }
            }
            return temp;
        }
        if (k == 8){
            string temp = "";
            for (int i = 0; i < n; i++){
                temp += '9';
            }
            for (int i = 0; i < 3 && i < n; i++){
                temp[i] = '8';
                temp[n - 1 - i] = '8';
            }
            return temp;
        }
        return "";
    }
};
