    bool isdivisibleby7(string &str){

        int carry = 0;
        for(int i=0;i<str.size();i++){
            int num = str[i]-'0';
            num = carry*10+num;
            carry = num%7;
        }

        return carry==0;
    }
    string largestPalindrome(int n, int k) {

        // long long num = 0;
        // for(long long i=10000000;i<100000000;i++){
        //     if(ispali(i) && i%7==0){
        //         num = i;
        //     }
        // }
        // cout<<num;

        string final_string;
        if(k==1 || k==3 || k==9){
            while(n--) final_string+='9';
        }else if(k==2){
            while(n--) final_string+='9';
            final_string[0]='8';
            final_string.back()='8';
        }else if(k==4){
            int temp_n = n;
            while(n--) final_string+='8';
            for(int k=2;k<temp_n-2;k++) final_string[k]='9';
        }else if(k==5){
            while(n--) final_string+='9';
            final_string[0]='5';
            final_string.back()='5';
        }else if(k==6){
            if(n%2==1){
                int temp_n = n;
                if(n==1) final_string = '6';
                else{
                   while(n--) final_string+='9';
                   final_string[0]='8';
                   final_string.back()='8';
                   final_string[temp_n/2]='8';
                }
            }else{
                if(n==2) final_string = "66";
                else{
                    int temp_n = n;
                    while(n--) final_string+='9';
                    final_string[0]='8';
                    final_string[temp_n-1]='8';
                    final_string[temp_n/2]='7';
                    final_string[(temp_n-1)/2]='7';
                }
            }
        }else if(k==7){
            // if(n==1) final_string = "7";
            // else if(n==2) final_string = "77";
            // else if(n==3) final_string = "959";
            // else if(n==4) final_string = "9779";
            // else if(n==5) final_string = "99799";
            // else if(n==6) final_string = "999999";
            // else if(n==7) final_string = "9994999";
            // else if(n==8) final_string = "99944999";

            int temp_n = n;
            while(n--) final_string += '9';

            if(temp_n%2==1){
                for(char ch='9';ch>='1';ch--){
                    final_string[temp_n/2]=ch;
                    if(isdivisibleby7(final_string)){
                        break;
                    }
                }
            }
            else{
                for(char ch='9';ch>='1';ch--){
                    final_string[temp_n/2]=ch;
                    final_string[(temp_n-1)/2]=ch;
                    if(isdivisibleby7(final_string)){
                        break;
                    }
                }
            }

        }else if(k==8){
            if(n==1) final_string = "8";
            else if(n==2) final_string = "88";
            else{
                int temp_n = n;
                while(n--) final_string+='9';
                final_string[0]='8';
                final_string[1]='8';
                final_string[2]='8';
                final_string[temp_n-1]='8';
                final_string[temp_n-2]='8';
                final_string[temp_n-3]='8';
            }
        }
        return final_string;
    }
