class Solution {
    public String largestPalindrome(int n, int k) {
        StringBuilder result = new StringBuilder();
        int i;
        if(k == 1 || k == 3 || k == 9) {
            for(i = 0; i < n; i += 1) {
                result.append('9');
            }
            return result.toString();
        }
        if(k == 2) {
            for(i = 0; i < n; i += 1) {
                if(i == 0 || i == n - 1) {
                    result.append('8');
                }
                else {
                    result.append('9');
                }
            }
            return result.toString();
        }
        if(k == 4) {
            for(i = 0; i < n; i += 1) {
                if(i == 0 || i == 1 || i == n - 2 || i == n - 1) {
                    result.append('8');
                }
                else {
                    result.append('9');
                }
            }
            return result.toString();
        }
        if(k == 5) {
            for(i = 0; i < n; i += 1) {
                if(i == 0 || i == n - 1) {
                    result.append('5');
                }
                else {
                    result.append('9');
                }
            }
            return result.toString();
        }
        if(k == 6) {
            if(n == 1) {
                return "6";
            }
            if(n == 2) {
                return "66";
            }
            if(n%2 == 0) {
                for(i = 0; i < n; i += 1) {
                    if(i == 0 || i == n - 1) {
                        result.append('8');
                    }
                    else if(i == n/2 || i == n/2 - 1) {
                        result.append('7');
                    }
                    else {
                        result.append('9');
                    }
                }
            }
            else {
                for(i = 0; i < n; i += 1) {
                    if(i == 0 || i == n - 1 || i == n/2) {
                        result.append('8');
                    }
                    else {
                        result.append('9');
                    }
                }
            }
            return result.toString();
        }
        if(k == 8) {
            for(i = 0; i < n; i += 1) {
                if(i == 0 || i == 1 || i == 2 || i == n - 3|| i == n - 2 || i == n - 1) {
                    result.append('8');
                }
                else {
                    result.append('9');
                }
            }
            return result.toString();
        }
        if(k == 7) {
            if(n == 1) {
                return "7";
            }
            if(n == 2) {
                return "77";
            }
            String[] pattern = new String[]{"5", "77", "7", "9", "4", "44", "6", "44", "4", "9", "7", "77"};
            String mid = pattern[(n - 3)%pattern.length];
            for(i = 0; i < (n - mid.length())/2; i += 1) {
                result.append('9');
            }
            result.append(mid);
            for(i = 0; i < (n - mid.length())/2; i += 1) {
                result.append('9');
            }
            if(result.length() < n) {
                result.append('9');
            }
            return result.toString();
        }
        return "invalid";
    }
}
