class Solution {
public:

    bool isDivisibleBy7(string &num_str) {
        long long remainder = 0;
        for (char c : num_str) {
            remainder = (remainder * 10 + (c - '0')) % 7;
        }
        return remainder == 0;
    }
    string largestPalindromicDivisibleBy7(int n, string p) {

        if (isDivisibleBy7(p)) return p;
        if(n%2) {
            for (int i = 9; i >= 0; --i) {
                string c=p;
                c[n/2]=i+'0';
                // cout<<c<<endl;
                if (isDivisibleBy7(c)) {
                    return c;
                }
            }

        }
        else {
            for (int i = 9; i >= 0; --i) {
                string c=p;
                c[n/2]=i+'0';
                c[n/2-1]=i+'0';
                if (isDivisibleBy7(c)) {
                    return c;
                }
            }

        }

        return p;
    }

    string largestPalindrome(int n, int k) {
        string an="";
        for(int i=0;i<n;i++) an+='9';

        if(k==1 || k==3 || k==9) return an;
        if(k==2) {
            an[0]='8';
            an[n-1]='8';
            return an;
        }
        if(k==5) {
            an[0]='5';
            an[n-1]='5';
            return an;
        }
        if(k%4==0) {
            an[n-1]='8';
            an[0]='8';
            if(n>1) {
                an[1]='8';
                an[n-2]='8';
            }
            if(k==4) return an;
        }
        if(k==8) {
            if(n>2) {
                an[2]='8';
                an[n-3]='8';
            }
            return an;
        }

        if(k==6) {
            if(n==1) return "6";
            if(n==2) return "66";
            an[0]='8';
            an[n-1]='8';
            if(n%2==1)
            an[n/2]='8';
            else {
                an[n/2]='7';
                an[n/2-1]='7';
            }
            return an;
        }
        if(n==1) return "7";
        if(n==2) return "77";

        return largestPalindromicDivisibleBy7(n, an);


    }
};
