import sys
sys.set_int_max_str_digits(100000)
class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:

        if k == 1:
            return '9' * n
        if k == 2:
            if n <= 2:
                return '8' * n
            else:
                return '8' + '9' * (n-2) + '8'
        if k == 3:
            return '9' * n
        if k == 4:
            if n <= 4:
                return '8' * n 
            else:
                return '88' + '9' * (n-4) + '88'

        if k == 5:
            if n == 1:
                return '5'
            return '5' + '9' * max(0, n-2) + '5'

        if k == 6:
            if n == 1:
                return '6'
            if n == 2:
                return '66'
            if n == 3:
                return '888'
            if n % 2:
                return '8' + '9' * (n // 2 - 1) + '8' + '9' * (n // 2 - 1) + '8'
            return '8' + '9' * (n // 2 - 2) + '77' + '9' * (n // 2 - 2) + '8'
        if k == 7:
            if n == 1:
                return '7'
            if n == 2:
                return '77'
            if n == 3:
                return '959'
            if n == 4:
                return '9779'
            if n % 2 == 1:
                for i in [9, 8, 7, 6, 5, 4, 3, 2, 1]:
                    if int('9' * (n // 2) + str(i) + '9' * (n //2)) % 7 == 0:
                        return '9' * (n // 2) + str(i) + '9' * (n //2)
            else:
                for i in [99, 88, 77, 66, 55, 44, 33, 22, 11]:
                    if int('9' * (n // 2 -1) + str(i) + '9' * (n //2 -1)) % 7 == 0:
                        return '9' * (n // 2-1) + str(i) + '9' * (n //2-1)
        if k == 8:
            if n <= 6:
                return '8' * n 
            else:
                return '888' + '9' * (n-6) + '888'
        if k == 9:
            return '9' * n
