class Solution {
public:
    string largestPalindrome(int n, int k) {
        if(k == 1 || k==3 || k== 9) {
            return string(n, '9');
        }else if(k == 2) {
            if(n == 1) return "8";
            if(n==2) return "88";
            return "8" + string(n-2, '9') + "8";
        }else if(k==5) {
            if(n==1) return "5";
            if(n==2) return "55";
            return "5" + string(n-2, '9') + "5";
        }else if(k==8) {
            if(n <= 6) return string(n, '8');
            return "888" + string(n-6, '9') + "888";
        }else if(k==4) {
            if(n <= 4) return string(n, '8');
            return "88" + string(n-4, '9') + "88";
        }else if(k == 6) {
            if(n <= 2) return string(n, '6');
            if(n % 2) {
                return "8" + string((n-3)/2, '9') + "8" + string((n-3)/2, '9') + "8";
            }
            return "8" + string((n-4)/2, '9') + "77" + string((n-4)/2, '9') + "8";
        }else {
            if(n<=2) return string(n, '7');
            string s = string(n, '9');
            if(n % 2) {
                for(int k=9; k>=0; k--) {
                    int rem = 0; s[n/2] = (char)('0' + k);
                    for(int i=0; i<n; i++) {
                        rem = (rem*10 + s[i] - '0') % 7;
                    }
                    if(rem == 0) return s;
                }
            }else {
                for(int k=9; k>=0; k--) {
                    int rem = 0; s[n/2] = s[n/2-1] = (char)('0' + k); 
                    for(int i=0; i<n; i++) {
                        rem = (rem*10 + s[i] - '0') % 7;
                    }
                    if(rem == 0) return s;
                }
            }
        }
        return "";
    }
};
