class Solution {
    public String largestPalindrome(int n, int k) {
        if (k == 1) {
            return "9".repeat(n);
        } else if (k == 2) {
            if (n <= 2) {
                return "8".repeat(n);
            } else {
                return "8" + "9".repeat(n - 2) + "8";
            }
        } else if (k == 3 || k == 9) {
            return "9".repeat(n);
        } else if (k == 4) {
            if (n <= 4) {
                return "8".repeat(n);
            } else {
                return "88" + "9".repeat(n - 4) + "88";
            }
        } else if (k == 5) {
            if (n <= 2) {
                return "5".repeat(n);
            } else {
                return "5" + "9".repeat(n - 2) + "5";
            }
        } else if (k == 6) {
            if (n <= 2) {
                return "6".repeat(n);
            } else if (n == 3) {
                return "888";
            } else if (n == 4) {
                return "8778";
            } else if (n % 2 == 1) {
                int l = n / 2 - 1;
                return "8" + "9".repeat(l) + "8" + "9".repeat(l) + "8";
            } else {
                int l = n / 2 - 2;
                return "8" + "9".repeat(l) + "77" + "9".repeat(l) + "8";
            }
        } else if (k == 8) {
            if (n <= 6) {
                return "8".repeat(n);
            } else {
                return "888" + "9".repeat(n - 6) + "888";
            }
        } else {
            String[] dic = {"", "7", "77", "959", "9779", "99799", "999999", "9994999",
                            "99944999", "999969999", "9999449999", "99999499999"};
            int l = n / 12;
            int r = n % 12;
            return "999999".repeat(l) + dic[r] + "999999".repeat(l);
        }
    }
}
