class Solution {
public:
    string largestPalindrome(int n, int k) {
        string res="";

        if(n==1) {
            for(int i=9;i>=1;i--) {
                if(i%k==0) {
                    return to_string(i);
                }
            }
        }

        if(k==1 or k==9 or k==3) {
            for(int i=0;i<n;i++) {
                res+='9';
            }
            return res;
        } else if(k==5) {
            res+='5';
            for(int i=0;i<n-2;i++) {
                res+='9';
            }
            res+='5';
            return res;
        } else if(k==2) {
            res+='8';
            for(int i=0;i<n-2;i++) {
                res+='9';
            }
            res+='8';
            return res;
        } else if(k==6) {
            if(n==2) {
                res="66";
            } else {
                int rem=0;
                res+='8';
                rem=8%6;
                for(int i=0;i<(n-2);i++) {
                    res+='9';
                    rem=(rem+9)%6;
                }
                res+='8';
                rem=(rem+8)%6;
                if(n%2) {
                    res[n/2]='9'-rem;
                } else {
                    if(rem%2) {
                        for(int i=0;i<n;i++) {
                            res[i]='6';
                        }
                    } else {
                        res[n/2-1]=res[n/2]='9'-rem/2;
                    }
                }
            }
            return res;
        } else if(k==4) {
            if(n<=3) {
                for(int i=0;i<n;i++) {
                    res+='8';
                }
            } else {
                res+='8';
                for(int i=0;i<(n-2);i++) {
                    res+='9';
                }
                res+='8';

                res[1]=res[n-2]='8';

            }
            return res;
        } else if(k==8) {
            res+='8';
            for(int i=0;i<n-2;i++) {
                res+='9';
            }
            res+='8';
            for(int i=0;i<min(n,3);i++) {
                res[i]='8';
            }
            for(int i=n-1;i>=max(0,n-3);i--) {
                res[i]='8';
            }
            return res;
        } else if(k==7) {
            int rem=0;
            vector<int>p10(n+1,1);
            for(int i=1;i<=n;i++) {
                p10[i]=p10[i-1]*10%7;
            }
            for(int i=0;i<n;i++) {
                res+='9';
                rem=(rem+9*p10[i])%7;
            }
            for(int i=9;i>=0;i--) {
                if(n%2==0) {
                    int h1=n/2-1,h2=n/2;
                    if((rem-9*p10[h1]-9*p10[h2]+i*p10[h1]+i*p10[h2]+14)%7==0) {
                        res[h1]=res[h2]=i+'0';
                        break;
                    }
                } else {
                    int half=n/2;
                    if((rem-9*p10[half]+i*p10[half]+7)%7==0) {
                        res[half]=i+'0';
                        break;
                    }
                }
            }
            return res;
        }
        return "";
    }
};
