class Solution {
public:
    string largestPalindrome(int n, int k) {
        int tmp;
        string s("");
        switch(k) {
            case 1:
            return string(n, '9');
            case 2:
            if(n <= 2) return string(n, '8');
            return "8" + string(n - 2, '9') + "8";
            case 3:
            return string(n, '9');
            case 4:
            if(n <= 4) return string(n, '8');
            return "88" + string(n - 4, '9') + "88";
            case 5:
            if(n <= 2) return string(n, '5');
            return "5" + string(n - 2, '9') + "5";
            case 6:
            switch(n) {
                case 1: return "6";
                case 2: return "66";
                default:
                if(n & 1) return "8" + string((n - 3) >> 1, '9') + "8" + string((n - 3) >> 1, '9') + "8";
                return "8" + string((n - 4) >> 1, '9') + "77" + string((n - 4) >> 1, '9') + "8";
            }
            case 7:
            tmp = n % 12;
            switch(tmp) {
                case 0: break;
                case 1: s = "7"; break;
                case 2: s = "77"; break;
                case 3: s = "959"; break;
                case 4: s = "9779"; break;
                case 5: s = "99799"; break;
                case 6: s = "999999"; break;
                case 7: s = "9994999"; break;
                case 8: s = "99944999"; break;
                case 9: s = "999969999"; break;
                case 10: s = "9999449999"; break;
                case 11: s = "99999499999"; break;
            }
            return string(n / 12 * 6, '9') + s + string(n / 12 * 6, '9');
            case 8:
            if(n <= 6) return string(n, '8');
            return "888" + string(n - 6, '9') + "888";
            case 9:
            return string(n, '9');
        }
        return "";
    }
};
