class Solution {
    int a[100005][10];
    int dp[100005][10];
    int pre[100005][10];
    char str[100006];

public:
    string largestPalindrome(int n, int k) {
        memset(dp, -1, sizeof(dp));
        memset(pre, -1, sizeof(pre));
        for (int j = 0; j < 10; j++) {
            int p = j;
            for (int i = 1; i <= n; i++) {
                a[i][j] = p % k;
                p %= k;
                p *= 10;
            }
        }

        int m = n / 2;
        string s1;

        int p = 0;
        string s;
        int now = 0;

        if (n % 2 == 1) {
            dp[m + 2][0] = 0;
            for (int j = 0; j < 10; j++) {
                if (dp[m + 2][j] == -1)
                    continue;
                for (int l = 0; l < 10; l++) {
                    int mod = (a[m + 1][l] + j) % k;
                    if (l > dp[m + 1][mod]) {
                        dp[m + 1][mod] = max(l, dp[m + 1][mod]);
                        pre[m + 1][mod] = j;
                    }
                }
            }
        } else {
            dp[m + 1][0] = 0;
        }

        for (int i = m; i > 0; i--) {
            for (int j = 0; j < 10; j++) {
                if (dp[i + 1][j] == -1)
                    continue;
                for (int l = 0; l < 10; l++) {
                    int mod = (a[i][l] + a[n - i + 1][l] + j) % k;
                    if (l > dp[i][mod]) {
                        dp[i][mod] = max(l, dp[i][mod]);
                        pre[i][mod] = j;
                    }
                }
            }
        }
        int nn=n-1;

        for (int i = 1; i <= m; i++) {
            now = dp[i][p];
            p = pre[i][p];
            char c= '0' + now;
            str[nn--]=c;
            str[i-1]=c;
            //s=s+c;
        }
        if (n % 2) {
            str[nn--]='0'+dp[m+1][p];
           // s1 = '0' + dp[m + 1][p];
        }
        string s2;
        for (auto c : s) {
            s2 = c + s2;
        }
        cout<<str<<endl;
        for(int i=0;i<n;i++)s+=str[i];
        s = s + s1 + s2;
        return s;
    }
};
