class Solution:
    import sys
    sys.set_int_max_str_digits(99999999)
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 9 or k == 3:
            return '9' * n
        if n == 1:
            if k == 1 or k == 3:
                return "9"
            if k == 2 or k == 4:
                return "8"
            return str(k)

        if k == 5:
            return "5" + '9' * (n - 2) + "5"
        if k == 2:
            return "8" + '9' * (n - 2) + "8"
        if k == 4:
            if n <= 4:
                return '8' * n
            return "88" + '9' * (n - 4) + "88"

        if k == 8:
            if n <= 6:
                return '8' * n
            return "888" + '9' * (n - 6) + "888"

        if k == 7:
            if n <= 2:
                return '7' * n
            if n % 2 == 1:
                u = (n -1) // 2
                for i in range(ord('9'), ord('0') - 1, -1):
                    s = ('9' * u + chr(i) + '9' * u)
                    if int(s) % 7 == 0:
                        return s
            else:
                u = (n - 2) // 2
                for i in range(ord('9'), ord('0') - 1, -1):
                    s = ('9' * u + chr(i) * 2 + '9' * u)
                    if int(s) % 7 == 0:
                        return s

        if k == 6:
            if n <= 2:
                 return '6' * n
            if n % 2:
                u = (n - 3) // 2
                return "8" + '9' * u + "8" + '9' * u + "8"
            else:
                u = (n - 4) // 2
                return "8" + '9' * u + "77" + '9' * u + "8"

        return ""
