class Solution {
public:
  string largestPalindrome(int n, int k) {
    if(n <= 6)
    {
      int a = 0, b = 0, q = 1;
      for(int i = 0; i < n; ++i)
      {
        a += q;
        b += q * 9;
        q *= 10;
      }
      for(int i = b; i >= a; --i)
      {
        char s[7];
        sprintf(s, "%d", i);
        bool ok = true;
        for(int j = 0; j * 2 < n; ++j)
          if(s[j] != s[n - 1 - j]) ok = false;
        if(ok && i % k == 0) return s;
      }
      return {};
    }

    string res(n, '9');
    if(k == 1) return res;
    int l = (n % 2 == 0) ? (n / 2 - 3) : (n / 2 - 2);
    int r = (n % 2 == 0) ? (n / 2 + 3) : (n / 2 + 3);
    int c = r - l;
    if(k == 5) res[0] = res[n - 1] = '5';
    else if(k % 2 == 0)
    {
      res[0] = res[n - 1] = '8';
      if(k % 8 == 0)
      {
        if(l > 1) res[1] = res[n - 2] = '8';
        if(l > 2) res[2] = res[n - 3] = '8';
      }
      else if(k % 4 == 0)
      {
        if(l > 1) res[1] = res[n - 2] = '8';
      }
    }

    cout << l << " " << r << "\n";

    int q = 1;
    int a = 0, b = 0;
    for(int i = 0; i < n; ++i)
    {
      if(i >= l && i < r)
      {
        if(i == l) b = q;
      }
      else a += q * (res[i] - '0');
      q = (q * 10) % k;
    }

    int t = 0;
    q = 1;
    for(int i = 0; i < c; ++i)
    {
      t += q * 9;
      q *= 10;
    }

    for(int i = t; i >= 0; --i)
    {
      char s[7] = {};
      int e = i;
      for(int j = c - 1; j >= 0; --j)
      {
        s[j] = '0' + e % 10;
        e /= 10;
      }
      bool ok = true;
      for(int j = 0; j * 2 < c; ++j)
        if(s[j] != s[c - 1 - j]) ok = false;
      if(!ok) continue;
      if((a + i * b) % k == 0)
      {
        for(int j = 0; j < c; ++j)
          res[l + j] = s[j];
        return res;
      }
    }

    return {};
  }
};
