class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        n2 = (n + 1) // 2
        vmax = 10 ** n2 - 1
        vmin = 10 ** (n2 - 1)
        if k == 5:
            vmax = 10 ** (n2 - 1) * 6 - 1
        if k == 2 or k == 6:
            vmax = 10 ** (n2 - 1) * 9 - 1
        if k == 4 and n2 > 5:
            vmax = 10 ** (n2 - 2) * 89 - 1
        if k == 8 and n2 > 5:
            vmax = 10 ** (n2 - 3) * 889 - 1
        for v in range(vmax, vmin - 1, -1):
            vs = str(v)[::-1]
            if n % 2:
                vs = vs[1:]
            vv = int(str(v) + vs)
            if vv % k == 0:
                return str(vv)
        return ''
