class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        sys.set_int_max_str_digits(10**5)
        if k == 1:
            return '9' * n
        elif k == 2:
            if n <= 2:
                return '8' * n
            return '8' + '9' * (n-2) + '8'
        elif k == 3:
            return '9' * n
        elif k == 4:
            if n <= 4:
                return '8' * n
            return '88' + '9' * (n-4) + '88'
        elif k == 5:
            if n == 1:
                return '5'
            return '5' + '9' * (n-2) + '5'
        elif k == 6:
            if n == 1:
                return '6'
            if n == 2:
                return '66'
            if n == 3:
                return '888'
            if n % 2 == 0:
                return '8' + '9' * ((n + 1)//2-2) + '77' + '9' * ((n + 1)//2-2) + '8'
            else:
                return '8' + '9' * ((n + 1)//2-2) + '8' + '9' * ((n + 1)//2-2) + '8'
        elif k == 7:
            if n == 1:
                return '7'
            if n == 2:
                return '77'
            base = '9' * n
            base_list = list(base)
            for i in range(n // 2, -1, -1):
                if n % 2 == 0:
                    for j in range(9, 0, -1):
                        base_list[i] = base_list[n-1-i] = str(j)
                        if int(''.join(base_list)) % 7 == 0:
                            return ''.join(base_list)
                else:
                    for j in range(9, 0, -1):
                        base_list[i] = str(j)
                        if int(''.join(base_list)) % 7 == 0:
                            return ''.join(base_list)
        elif k == 8:
            if n <= 6:
                return '8' * n
            return '888' + '9' * (n-6) + '888'
        elif k == 9:
            return '9' * n
        return ""
