class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        print(n , k)
        if k == 1 : 
            return '9'*n
        if k == 2:
            if n >= 2:
                return  '8' + '9'*(n-2) + '8'
            else:
                return '8'
        if k == 3:
            return '9'*n 
        if k ==4:
            if n <= 4:
                return '8' * n
            t = n-4
            return '88' + '9'*t + '88'
        if k == 5:
            if n == 1:
                return '5'
            if n >=2:
                return '5' + '9' * (n-2) + '5'
        if k ==6:
            if n == 1:
                return '6'
            if n == 2:
                return '66'
            if n == 3:
                return '888'
            # if n == 4:
                # return '8778' '897798' '8998998'

            if n >=4 and n%2 == 0:
                t = int((n-4)/2)
                return '8' + '9' * t + '77' + '9' *t + '8' 
            if n >=4 and n%2 == 1:
                t = int((n-3)/2)
                return '8' + '9' * t + '8' + '9' *t + '8' 
        if k == 7:
            # '959' 9779  99799 999999 9994999 99944999
            if n <=2:
                return '7'*n
            t = int((n-1)/2)
            if n%2 == 1:
                for i in [9,8,7,6,5,4,3,2,1,0]:
                    p = '9'*t + str(i) + '9'*t
                    if judge7(p):
                        return p
            else:
                for i in [9,8,7,6,5,4,3,2,1,0]:
                    p = '9'*t + str(i)*2 + '9'*t
                    if judge7(p):
                        return p



        if k ==8:
            if n<7:
                return '8'*n
            t = n-6
            return '888' + '9'* t + '888'
        if k== 9:
            return '9'*n

        return 0 


