class Solution {
public:
    string solve3(int n){
        string s ;
        for(int i  = 0; i  < n ; i++){
            s+='9';
        }
        return s;
    }

    string solve2(int n ,int k){
        string s ;
        for(int i  = 0; i  < n ; i++){
            s+='8';
        }
        if(k==2){
            for(int i = 1 ; i < n-1;i++){
                s[i]='9';
            }
        }
        else if (k==4){
            for(int i = 2 ; i < n-2;i++){
                s[i]='9';
            }
        }
        else {
            for(int i = 3 ; i < n-3;i++){
                s[i]='9';
            }

        }
        return s;
    }
    string solve5(int n){
        string s;
        if(n==1) return "5";

        int half  = n/2;
        s.push_back('5');
        for(int i =1 ;i<half ; i++){
            s.push_back('9');
        }
        string t = s;
        reverse(t.begin(),t.end());
        if(n%2==1)s.push_back('9');
        s+=t;
        return s;
    }
    string solve6(int n){
        string s;
        if(n==1) return "6";
        else if(n==2)return "66";

        if(n==3) return "888";
        if(n==6) return "897798";

        if(n%2==0){
            int half = n/2;
            s = "8";
            for(int i = 2 ; i < half; i++){
                s.push_back('9');
            }
            s.push_back('7');
            string t = s;
            reverse(t.begin(),t.end());
            s+=t;
            return s;
        }
        else{
            int half = n/2;;
            s = "8";
            for(int i = 1 ; i< half;i++){
                s.push_back('9');
            }
            string t = s;
            s.push_back('8');
            reverse(t.begin(),t.end());
            s+=t;
            return s;
        }
     }

    bool check7(const string& num) {
    int n = num.length();
    if (n == 0) return false;
    if (n == 1) return ((num[0] - '0') % 7 == 0);

    int result = 0;
    int multiplier = 1;

    for (int i = n - 1; i >= 0; i -= 3) {
        int chunk = 0;
        int j = i;
        int pow10 = 1;

        for (; j > i - 3 && j >= 0; j--) {
            chunk += (num[j] - '0') * pow10;
            pow10 *= 10;
        }

        result += multiplier * chunk;
        multiplier *= -1;  
    }

    result = abs(result);

    return (result % 7 == 0);
}
    string solve7(int n){
        if(n==1) return "7";
        if(n==2) return "77";
        int half = n/2;
        string res ;
        if(n%2==0){
            string s;
            for(int i = 1 ; i < half;i++){
                s.push_back('9');
            }
            for(int i = 0 ; i <= 9 ; i++){
                s.push_back('0'+i);
                string t = s;
                reverse(t.begin(),t.end());
                string k = s+t;
                // s+=t;
                if(check7(k))res = k;
                s.pop_back();
            }

        }
        else{
            string s;
            for(int i = 0 ; i < half;i++){
                s.push_back('9');
            }

            for(int i = 0 ; i <= 9 ; i++){
                string t = s;
                s.push_back('0'+i);
                reverse(t.begin(),t.end());
                string k = s+t;
                // s+=t;
                if(check7(k))res = k;
                s.pop_back();
            }

        }
        return res;
    }

    string largestPalindrome(int n, int k) {
        if(k==1)return solve3(n);
        else if(k==2)return solve2(n,k);
        else if(k==3) return solve3(n);
        else if(k==4) return solve2(n,k);
        else if(k==5) return solve5(n);//
        else if(k==6) return solve6(n);//
        else if(k==7) return solve7(n);//
        else if (k==8) return solve2(n,k);
        else return solve3(n);
    }
};
