class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        sys.set_int_max_str_digits(100000)
        if k == 1 or k == 3 or k == 9:
            return '9' * n
        if k == 2:
            if n == 1:
                return '8'
            else:
                return '8' + '9' * (n - 2) + '8' 
        if k == 4:
            if n < 5:
                return '8' * n
            else:
                return '88' + '9' * (n - 4) + '88'
        if k == 8:
            if n < 7:
                return '8' * n
            else:
                return '888' + '9' * (n - 6) + '888'
        if k == 5:
            if n == 1:
                return '5'
            else:
                return '5' + '9' * (n - 2) + '5' 
        if k == 6:
            if n < 3:
                return '6' * n
            elif n % 2 == 0:
                mid = 99
                while True:
                    if int('8' + ('9' * ((n - 3) // 2)) + str(mid) + ('9' * ((n - 3) // 2)) + '8') % 6 == 0:
                        return '8' + ('9' * ((n - 3) // 2)) + str(mid) + ('9' * ((n - 3) // 2)) + '8'
                    else:
                        mid -= 11
            else:
                mid = 9
                while True:
                    if int('8' + ('9' * ((n - 3) // 2)) + str(mid) + ('9' * ((n - 3) // 2)) + '8') % 6 == 0:
                        return '8' + ('9' * ((n - 3) // 2)) + str(mid) + ('9' * ((n - 3) // 2)) + '8'
                    else:
                        mid -= 1    
        if k == 7:
            if n % 2 == 0:
                mid = 99
                while True:
                    if int('9' * ((n - 1) // 2) + str(mid) + '9' * ((n - 1) // 2)) % 7 == 0:
                        return '9' * ((n - 1) // 2) + str(mid) + '9' * ((n - 1) // 2)
                    else:
                        mid -= 11
            else:
                mid = 9
                while True:
                    if int('9' * ((n - 1) // 2) + str(mid) + '9' * ((n - 1) // 2)) % 7 == 0:
                        return '9' * ((n - 1) // 2) + str(mid) + '9' * ((n - 1) // 2)
                    else:
                        mid -= 1
