class Solution {
public:
    vector<int> f[10],mi;
    int m,kk;
    string a;
    bool dfs(int no,int s){
        if(no==(m+1)/2)return s==0;
        if(f[s][no])return f[s][no]-1;
        for(int i=9;i>=0;--i){
            int ss=(mi[m-no-1]+((m%2==1&&no+1==(m+1)/2)?0:mi[no]))*i%kk;
            //cout<<no<<' '<<mi[m-no-1]+((m%2==1&&no+1==(m+1)/2)?0:mi[no])<<'\n';
            bool fl=dfs(no+1,(s-ss+kk)%kk);
            if(fl){
                f[s][no]=2;
                return 1;
            }
        }
        f[s][no]=1;
        return 0;
    }
    void fd(int no,int s){
        if(no==(m+1)/2)return;
        for(int i=9;i>=0;--i){
            int ss=(mi[m-no-1]+((m%2==1&&no+1==(m+1)/2)?0:mi[no]))*i%kk;
            bool fl=dfs(no+1,(s-ss+kk)%kk);
            if(fl){
                a[m-no-1]=a[no]='0'+i;
                fd(no+1,(s-ss+kk)%kk);
                return;
            }
        }
    }
    string largestPalindrome(int n, int k) {
        m=n;kk=k;
        a.clear();
        a.resize(n);
        if(n==1){
            for(int i=9;i>=1;--i){
                if(i*k<10){
                    k=i*k;
                    break;
                }
            }
            a[0]='0'+k;
            return a;
        }
        mi.clear();
        mi.resize(n);
        mi[0]=1;
        for(int i=1;i<n;++i){
            mi[i]=mi[i-1]*10%k;
        }
        for(int i=0;i<k;++i){
            f[i].clear();
            f[i].resize(n);
        }
        for(int i=9;i;--i){
            int t=(mi[0]+mi[n-1])*i%k;
            bool fl=dfs(1,(k-t)%k);
            if(fl){
                cout<<t<<' '<<i<<'\n';
                a[0]=a[n-1]='0'+i;
                fd(1,(k-t)%k);
                return a;
            }
        }
        return a;
    }
};
