class Solution {
public:
    string sub(string s, int i) {
        string si = to_string(i);
        int size = si.size();
        string subs = s.substr(s.size() - size);
        int now = stoi(subs);
        if (now < i) {
            subs = s.substr(s.size() - size - 1);
        }

        int after = stoi(subs) - i;
        return s.substr(0, s.size() - subs.size()) + to_string(after);
    }

    bool check(string& s, int k) {
        int left = 0;
        for (char c : s) {
            left = (left * 10 + (c - '0')) % k;
        }
        return left == 0;
    }



    string largestPalindrome(int n, int k) {
        string first((n + 1) / 2, '9');

        string second((n + 1) / 2, '0');

        int ma = 0;

        for (int i = 0; i < 1000; ++i) {
            int now = i * k;
            string s = to_string(now);
            if (s.back() == '0') continue;
            reverse(s.begin(), s.end());
            s = s.substr(0, min(first.size(), s.size()));
            int size = s.size();
            if (stoi(s) > stoi(second.substr(0, size))) {
                second = s + second.substr(size);
                first = s + string(first.size() - size, '9');
            }
            // cout << first << endl;
        }
        // first.back() = '9';
        // first[0] = '0' + ma;
        // cout << "ma: " << ma << endl;
        for (int i = 3; i < first.size(); ++i) {
            first[i] = '9';
        }
        // cout << first << endl;

        for (int i = 0; i <= 100; ++i) {
            string tmp1 = sub(first, i);
            string tmp2 = tmp1;
            reverse(tmp2.begin(), tmp2.end());
            string s;
            if (n & 1) {
                s = tmp1 + tmp2.substr(1);
            } else {
                s = tmp1 + tmp2;
            }
            if (check(s, k)) {
                // cout << s.size() << endl;
                return s;
            }
        }

        return "0";
    }
