class Solution {
    bool check(string &s){
        int n = s.size();
        int num = 0;
        for(int i=0; i<n; i++){
            num *= 10;
            num += (s[i]-'0');
            num %= 7;
        }
        return num==0;
    }
public:
    string largestPalindrome(int n, int k) {
        string ans;
        for (int i = 0; i < n; i++)
            ans.push_back('9');
        if (k == 1 || k == 3 || k == 9)
            return ans;
        if (k == 2) {
            ans[0] = '8';
            ans[n - 1] = '8';
        } else if (k == 4) {
            ans[0] = '8';
            ans[n - 1] = '8';
            if (n > 1) {
                ans[1] = '8';
                ans[n - 2] = '8';
            }
        } else if (k == 5) {
            ans[0] = '5';
            ans[n - 1] = '5';
        } else if (k == 6) {
            if (n & 1) {
                if(n==1)    ans[0] = '6';
                else{
                    ans[0] = '8';
                    ans[n-1] = '8';
                    ans[n/2] = '8';
                }
            } else {
                if(n==2)    ans = "66";
                else{
                    ans[0] = '8';
                    ans[n-1] = '8';
                    ans[n/2] = '7';
                    ans[n/2-1] = '7';
                }
            }
        } else if (k == 7) {
            if(n==1)    ans = "7";
            else if(n==2)   ans = "77";
            else{
                for(int i=9; i>=0; i--){
                    ans[n/2] = '0'+i;
                    ans[(n-1)/2] = '0'+i;
                    if(check(ans))  break;
                }
            }
        } else if (k == 8) {
            ans[0] = '8';
            ans[n - 1] = '8';
            if (n > 1) {
                ans[1] = '8';
                ans[n - 2] = '8';
            }
            if (n > 2) {
                ans[2] = '8';
                ans[n - 3] = '8';
            }
        }
        return ans;
    }
};
