class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        def check(s):
            v = 0
            for i, x in enumerate(s):
                v = v * 10 + int(x)
                v = v % 7
            # print(s, v)
            return v == 0

        if k == 1: return ''.join(['9'] * n)
        if k == 2: 
            if n <= 2: return ''.join(['8'] * n)
            return ''.join(['8']  + ['9'] * (n-2) + ['8'] )
        if k == 3: return ''.join(['9'] * n)
        if k == 4:
            if n <= 4: return ''.join(['8'] * n)
            return ''.join(['8', '8'] + ['9'] * (n-4) + ['8', '8'])
        if k == 5:
            if n <= 2: return ''.join(['5'] * n)
            return ''.join(['5'] + ['9'] * (n-2) + ['5'])
        if k == 6:
            if n == 1: return '6'
            if n == 2: return '66'
            if n % 2: return '8' +  ''.join(['9'] * ((n-3)//2)) +  '8' + ''.join(['9'] * ((n-3)//2)) + '8'
            return '8' +  ''.join(['9'] * ((n-4)//2)) +  '77' + ''.join(['9'] * ((n-4)//2)) + '8'
        if k == 7:
            if n <= 2: return ''.join(['7'] * n)
            if n == 3: return '959'
            if n == 4: return '9779'
            if n == 5: return '99799'
            if n == 6: return '999999'
            if n == 7: return '9994999'
            if n == 8: return '99944999'
            if n == 9: return "999969999"
            if n == 10: return "9999449999"
            if n == 11: return "99999499999"
            if check(''.join(['9'] * n)): return ''.join(['9'] * n)
            if n % 2:
                for i in range(8, -1, -1):
                    s1 = ['9'] * ((n-1)//2) + [str(i)] + ['9'] * ((n-1)//2)
                    if check(''.join(s1)): return ''.join(s1)
            else:
                for i in range(8, -1, -1):
                    s1 = ['9'] * ((n-2)//2) + [str(i), str(i)] + ['9'] * ((n-2)//2)
                    if check(''.join(s1)): return ''.join(s1)
        if k == 8:
            if n <= 6: return ''.join(['8'] * n)
            return ''.join(['8'] * 3 + ['9'] * (n-6) + ['8'] * 3)
        if k == 9: return ''.join(['9'] * n)
