class Solution {
public:
    bool check( string str){
        int carry = 0;
        for( char ch : str){
            int num = carry * 10 + (ch - '0');
            carry = num % 7;
        }
        return carry == 0;
    }
    bool check1(string str , int div){
        int carry = 0;
        for( char ch : str){
            int num = carry * 10 + (ch - '0');
            carry = num % div;
        }
        return carry == 0;
    }
    string largestPalindrome(int n, int k) {
        if( n == 1){
            for( int i = 9; i ; --i){
                if( i % k == 0) return to_string(i);
            }
        }
        string ans= "";
        if( k == 1 or k == 3 or k == 9){
            for( int i = 0; i < n; ++i)   ans += '9';
        }
        else if( k == 2){
            ans += '8';
            for( int i = 0; i < n-2; ++i)   ans += '9';
            ans += '8';
        }
        else if( k == 4){
            if( n <= 4){
                for( int i = 0; i < n; ++i) ans += '8';
            }
            else{
                ans += "88";
                for( int i = 0;i < n-4; ++i)    ans += "9";
                ans += "88";
            }

        }
        else if( k == 5){
            ans += '5';
            for( int i = 0; i < n-2; ++i)   ans += '9';
            ans += '5';
        }
        else if( k == 6){
            if( n % 2){
                ans += '8';
                int sum = 1;
                for( int i = 1; i < n/2; ++i){
                    ans  += '9';
                    sum = (sum + 18) % 3;
                }
                string temp = ans;
                reverse(temp.begin(), temp.end());
                ans += char( '0' + (9 - sum));
                ans += temp;
            }
            else{
                if( n == 2) return "66";
                for( int i = 0; i < n; ++i){
                    if( i == 0 or i == n-1)
                        ans += '8';
                    else if( i == n/2 or i == n/2 - 1)
                        ans += '7';
                    else ans += '9';
                }
            }
        }
        else if( k == 7){
            string temp = "";
            for( int i = 0; i < (n-1)/2; ++i)   temp += '9';
            int rem =  2 - (n % 2);
            int found = 0;
            for( int i = 9; i >= 1; --i){
                char ch = char('0' + i);
                string another = temp;
                another += ch;
                if( rem == 2)   another += ch;
                another += temp;

                if(check(another)){
                    ans = another;
                    found = 1;
                    break;
                }
            }
            if( !found){
                for( int i = 0;i < n; ++i)  ans += '7';
            }
        }
        else if( k == 8){
            if( n <= 6){
                for( int i = 0; i < n; ++i) ans += '8';
            }
            else{
                ans += "888";
                for( int i = 0; i < n-6; ++i)   ans += '9';
                ans += "888";
            }
        }


        return ans;
    }
};
