class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        record = {
            1: 9,
            2: 8,
            3: 9,
            5: 5,
            9: 9

        }
        if k in record:
            if n == 1:
                return str(record[k])

            return str(record[k]) + "9" * (n-2) + str(record[k])

        if k == 4:
            if n <= 4:
                return "8" * n
            else:
                return "88" + "9" * (n-4) + "88"

        if k == 8:
            if n <= 6:
                return "8" * n
            else:
                return "888" + (n-6) * "9" + "888"

        if k == 6:
            if n == 1:
                return "6"
            elif n == 2:
                return "66"
            elif n % 2 != 0:
                return "8" + ((n-3) // 2) * "9" + "8" + ((n-3) // 2) * "9" + "8"
            else:
                return "8" + ((n-4) // 2) * "9" + "77" + ((n-3) // 2) * "9" + "8"

        if k == 7:
            if n == 1:
                return "7"
            elif n == 2:
                return "77"
            if n % 2 != 0:
                res = 0
                for i in range(10):
                    cur = int("9" * ((n-1)//2) + str(i) + "9" * ((n-1)//2))
                    if cur % 7 == 0:
                        res = max(res, cur)
                return str(res)
            else:
                res = 0
                for i in range(10, 100):
                    if str(i)[0] != str(i)[1]:
                        continue
                    cur = int("9" * ((n-2)//2) + str(i) + "9" * ((n-2)//2))
                    if cur % 7 == 0:
                        res = max(res, cur)
                return str(res)
