class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        sys.set_int_max_str_digits(100000)
        if k in [3, 1, 9]:
            return '9' * n
        if k == 5:
            if n == 1:
                return '5'
            return '5' + '9' * (n - 2) + '5'
        if k == 2:
            if n == 1:
                return '8'
            return '8' + '9' * (n - 2) + '8'
        if k == 4:
            if n == 1:
                return '8'
            if n == 2:
                return '88'
            if n == 3:
                return '888'
            if n == 4:
                return '8888'
            return '88' + '9' * (n - 4) + '88'
        if k == 6:
            if n == 1:
                return '6'
            if n == 2:
                return '66'
            if n == 3:
                return '888'
            if n == 4:
                return '8778'
            if n > 3:
                prefix = '8' + '9' * (n // 2 - 2 + n % 2)
                s = 16 + 9 * (n - (2 if n % 2 == 0 else 1))
                for mid_dig in range(9, -1, -1):
                    if n % 2 == 0:
                        if (s + mid_dig * 2)  % 3 == 0:
                            break
                    elif (s + mid_dig) % 3 == 0:
                        break
                return prefix + str(mid_dig) * (2 if n % 2 == 0 else 1) + prefix[::-1]
        if k == 7:
            if n == 1:
                return '7'
            if n == 2:
                return '77'
            if n == 3:
                return '959'
            if n == 4:
                return '9779'
            if n % 2 == 0:
                nines = '9' * (n // 2 - 1)
                nint = int(nines)
                #print(nines)
                templ = int(nines) + 10**(n//2 + 1) * nint
                #print(templ)
                for mid_dig in range(9, -1, -1):
                    mid_dig2 = 10*mid_dig + mid_dig
                    val = templ + mid_dig2 * (10**(n//2 - 1))
                    # print(val)
                    if val % 7 == 0:
                        return nines + str(mid_dig) + str(mid_dig) + nines
            if n % 2 == 1:
                nines = '9' * (n // 2)
                nint = int(nines)
                templ = int(nines) + 10**(n//2 + 1) * nint
                for mid_dig in range(9, -1, -1):
                    if (templ + mid_dig * (10**(n//2))) % 7 == 0:
                        return nines + str(mid_dig) + nines
        if k == 8:
            if n == 1:
                return '8'
            if n == 2:
                return '88'
            if n == 3:
                return '888'
            if n == 4:
                return '8888'
            if n == 5:
                return '88888'
            return '888' + '9' * (n - 6) + '888'
