class Solution {
public:
    string largestPalindrome(int n, int k) {
        vector<int> rec(n);
        int d = (n+1)/2;
        vector<int> dp(d);
        rec[0]=1;
        for(int i=1;i<n;++i){
            rec[i]=(rec[i-1]*10)%k;
        }
        for(int i=0;i<d;++i){
            if(i!=n-1-i) dp[i]=(rec[i]+rec[n-1-i])%k;
            else dp[i]=rec[i];
        }
        vector<int> dp_gcd(d+1);
        dp_gcd[d]=0;
        for(int i=d-1;i>=0;--i){
            dp_gcd[i]=gcd(dp_gcd[i+1],dp[i]);
        }
        // for(int i=0;i<d;++i) cout<<dp[i]<<endl;
        // cout<<endl;
        vector<int> ans(n);
        int target=0;
        for(int i=0;i<d;++i){
            int t;
            for(t=9;t>=0;--t){
                int tmp = dp_gcd[i+1];
                int flag=0;
                for(int dk=0;dk<=k;++dk){
                    int cur = (t*dp[i]+tmp*dk)%k;
                    if(cur==target){
                        flag=1;
                        break;
                    }
                }
                if(flag==1) break;
            }
            ans[i]=t;
            target=(((target-t*dp[i])%k)+k)%k;
            // cout<<target<<endl;
        }
        vector<char> res(n);
        for(int i=0;i<d;++i){
            res[i]=res[n-1-i]='0'+ans[i];
        }
        return string(res.begin(),res.end());
    }
};
