inline ll mul(ll x,ll y){
    x%=mod;y%=mod;
    x+=mod;y+=mod;
    return x*y %mod;
}

inline ll add(ll x,ll y){
    x%=mod;y%=mod;
    x+=mod;y+=mod;
    return (x+y) %mod;
}

inline ll power(ll x,ll y){
    x%=mod;
    ll res=1;
    while(y){
        if(y&1)
            res=res*x %mod;
        x=x*x %mod;
        y/=2;
    }
    return res;
}

class Solution {
public:
    ll n,k;
    string ans;
    vector<vector<ll>> dp;
    vector<ll> fact;
    bool rec(ll idx,ll s){
        if(~dp[idx][s] )return dp[idx][s];
        if(idx==(n)/2){
            if(n&1){
                ll s_tmp=s;
                for(ll j=9;j>=0;j--){
                    ans.pb('0'+j);

                    s=(s_tmp+j*fact[n-idx-1])%k;


                    if(s==0){
                        string tmp="";
                        for(ll i=n/2-1;i>=0;i--){
                            tmp+=ans[i];
                        }
                        ans+=tmp;
                        return dp[idx][s_tmp]=1;
                    }

                    ans.pop_back();

                }
                return dp[idx][s_tmp]=0;
            }else{
                if(s==0){
                    string tmp="";
                    for(ll i=n/2-1;i>=0;i--){
                        tmp+=ans[i];
                    }
                    ans+=tmp;
                }
                return dp[idx][s]=s==0;
            }
        }

        for(ll i=9;i>=0;i--){
            ans.pb('0'+i);
            if(rec(idx+1,(s+i*fact[idx]+i*fact[n-idx-1])%k))return dp[idx][s]=1;
            ans.pop_back();
        }

        return dp[idx][s]=0;
    }

    string largestPalindrome(int n_, int k_) {
        // string ans="";
        n=n_;
        k=k_;
        if(k==1||k==9||k==3){// 4
            rep(i,n)ans+='9';
            return ans;
        }else if(k==2){
            ans+='8';
            rep(i,n-2)ans+='9';
            if(n>=2)ans+='8';
            return ans;
        }else if(k==4){
            ans+='8';
            if(n>=4)ans+='8';
            if(n>=4)rep(i,n-4)ans+='9';
            else if(n>=2)rep(i,n-2)ans+='8';
            if(n>=2)ans+='8';
            if(n>=4)ans+='8';   
            return ans;
        }else if(k==5){
            ans+='5';
            rep(i,n-2)ans+='9';
            if(n>=2)ans+='5';
            return ans;
        }else if(k==8){
            ans+='8';
            if(n>=4)ans+='8';
            if(n>=6)ans+='8';
            if(n>=6)rep(i,n-6)ans+='9';
            else if(n>=4)rep(i,n-4)ans+='8';
            else if(n>=2)rep(i,n-2)ans+='8';
            if(n>=2)ans+='8';
            if(n>=4)ans+='8';   
            if(n>=6)ans+='8';   
            return ans;
        }
        // cout<<"ok";
        n=n_,k=k_;
        if(n==1){
            for(int i=9;i>=1;i--){
                string s="";
                s+='0'+i;
                if((i%k)==0)return s;
            }
        }
        fact.pb(1);
        rep(i,n){
            fact.pb(fact.back()*10 %k);
        }
        ans.clear();
        dp.clear();
        dp.resize(n,vector<ll> (k,-1));
        for(int i=9;i>=1;i--){
            ans.pb('0'+i);
            if(rec(1,(i*fact[0] + i*fact[n-1])%k)){
                return ans;
            }
            ans.pop_back();
        }
        return "-1";
    }
};
