class Solution {
public:
    bool isDivide(string num, int k) {
        long long left = 0;
        for (int i = 0; i < num.length(); ++i) {
            left = (left * 10 + num[i] - '0') % k;
        }
        return left == 0;
    }
    string largestPalindrome(int n, int k) {
        // if (n == 1) {
        //     string ans(1, '0');
        //     ans[0] += k;
        //     // cout << ans << '\n';
        //     return ans;
        // }
        // std::cout << isDivide("89998", 6) << '\n';
        string ans(n, '0');
        if (k == 1) {
            for (int i = 0; i < n; i++) ans[i] = '9';
        }
        else if (k == 2) {
            for (int i = 0; i < n; i++) ans[i] = '9';
            ans[0] = '8';
            ans[n - 1] = '8';
        }
        else if (k == 3) {
            for (int i = 0; i < n; i++) ans[i] = '9';
        }
        else if (k == 4) {
            if (n == 1) return "8";
            for (int i = 0; i < n; i++) ans[i] = '8';
            if (n > 3) {
                for (int i = 2; i < n - 2; i++) ans[i] = '9';
            }
        }
        else if (k == 5) {
            for (int i = 0; i < n; i++) ans[i] = '9';
            ans[0] = '5';
            ans[n - 1] = '5';
        }
        else if (k == 6) {
            ans[0] = '8';
            ans[n - 1] = '8';
            // if (n == 1) return ans;
            int count = 16;
            for (int i = 1; i < n / 2; i++) {
                ans[i] = '9';
                ans[n - 1 - i] = '9';
                count += 18;
            }
            if (n % 2) {
                int p = 6 - count % 6;
                if (p + 6 <= 9) {
                    ans[n / 2] = (6 - count % 6 + 6) + '0';
                }
                else {
                    ans[n / 2] = (6 - count % 6) + '0';
                }

            }
            else {
                int d = count % 6 / 2;
                ans[n / 2 - 1] -= d;
                ans[n / 2] -= d;
            }
            if (n == 1) ans[0] = '6';
        }
        else if (k == 7) {
            if (n == 1) ans[0] = '7';
            else if (n == 2) {
                ans[0] = '7';
                ans[1] = '7';
            }
            else {
                for (int i = 0; i < n; i++) ans[i] = '9';
                if (n % 2 == 0) {
                    for (int i = 9; i >= 1; i--) {
                        ans[n / 2] = '0' + i;
                        ans[n / 2 - 1] = '0' + i;
                        if (isDivide(ans, 7)) return ans;
                    }
                }
                else {
                    for (int i = 9; i >= 1; i--) {
                        // ans[n / 2] = '0' + i;
                        ans[n / 2] = '0' + i;
                        if (isDivide(ans, 7)) return ans;
                    }
                }
            }
        }
        else if (k == 8) {
            if (n == 1) return "8";
            for (int i = 0; i < n; i++) ans[i] = '8';
            if (n >= 7) {
                for (int i = 3; i < n - 3; i++) ans[i] = '9';
            }
            // ans[n / 2] = '8';
        }
        else if (k == 9) {
            for (int i = 0; i < n; i++) ans[i] = '9';
        }
        return ans;
    }
};
