class Solution {
public:
string k4(int n) {
    if (n <= 2) return string(n, '8');
    return "8" + string((n-2), '9') + "8";
}

string k8(int n) {
    if (n <= 4) return string(n, '8');
    return "88" + string((n-4), '9') + "88";
}

string k6(int n) {
    if (n == 1) return "8";
    string firstHalf = string((n-1) / 2, '9');

    if (n % 2 == 0) {
        string middle = "77";
        return firstHalf + middle + string(firstHalf.rbegin(), firstHalf.rend());
    } else {
        string middle = "8"; 
        return firstHalf + middle + string(firstHalf.rbegin(), firstHalf.rend());
    }
}

string subtractOne(string num) {
    int i = num.size() - 1;
    while (i >= 0 && num[i] == '0') {
        num[i] = '9';
        i--;
    }
    if (i >= 0) {
        num[i]--;
    }
    return num;
}

string generatePalindrome(string firstHalf, int n) {
    string palindrome = firstHalf;
    if (n % 2 == 0) {
        palindrome += string(firstHalf.rbegin(), firstHalf.rend());
    } else {
        palindrome += string(firstHalf.rbegin() + 1, firstHalf.rend());
    }
    return palindrome;
}

bool isDivisibleBy7(string num) {
    long long remainder = 0;
    for (char c : num) {
        remainder = (remainder * 10 + (c - '0')) % 7;
    }
    return remainder == 0;
}

string k7(int n) {
    string firstHalf((n + 1) / 2, '9');  // Start with the largest possible first half

    while (firstHalf.size() == (n + 1) / 2) {
        string palindrome = generatePalindrome(firstHalf, n);
        if (isDivisibleBy7(palindrome)) {
            return palindrome;
        }
        firstHalf = subtractOne(firstHalf);  // Decrement the first half
    }

    return "";  // Return an empty string if no valid palindrome is found
}

